import React, { useState } from 'react';

export default function Contact() {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = e.target;

    // Ternary statement that will call either setFirstName or setLastName based on what field the user is typing in
    return name === 'fulltName' ? setFullName(value) : setEmail(value);
  };

  const handleFormSubmit = (e) => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    e.preventDefault();

    // Alert the user their first and last name, clear the inputs
    alert(`Hello ${fullName} ${email}`);
    setFullName('');
    setEmail('');
  };
  return (
    <div>
      <div class="container">
        <p>Contact</p>
        <div class="row">
          <div class="col-9">
            <p>Let's Chat</p>
            <p>Fill in your info in the form below and I look forward to hearing from you!</p>
          </div>
          <form style={{background:'pink'}}class="col-9">
            <div class="mb-3">
              <label for="exampleFormControlInput1" class="form-label">Name</label>
              <input type="text" class="form-control" name="name" placeholder="Name"></input>
              <label for="exampleFormControlInput1" class="form-label">Email</label>
              <input type="email" class="form-control" name="email" placeholder="email"></input>
            </div>
            <div class="mb-3">
              <label for="exampleFormControlTextarea1" class="form-label">Message</label>
              <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
            </div>
            <div class="d-grid  col-4 mx-auto">
              <button type="button" class="btn btn-outline-light">Light</button>
            </div>
          </form>
          <div style={{background:'orange'}}class="col-3 ">.col-6 Subsequent columns continue along the new line.</div>
        </div>
      </div>
    </div>
  );
}
