import React, { useState } from 'react';

export default function Contact() {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = e.target; // since name is a const, it cannot change; however we are grabbing the e.target value by the name of the event
    // and then after targeting the event happening break and reset the switch name and then run that value which allows the const name to be used if different ways
    switch (name) { 
      case 'fullName':
        setFullName(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'message':
        setMessage(value);
        break;
        default:
          break;
    }


  };

  const handleFormSubmit = (e) => {
    // when the button is clicked the usee
    setFullName(fullName ? fullName : 'Default null')
    setEmail(email ? email : 'Default null')
    setMessage(message ? message : 'Default null')

    // Alert the user their first and last name, clear the inputs
    alert(`Hello ${fullName} | ${email} | ${message}`);
    setFullName('');
    setEmail('');
    setMessage('');
  };
  return (
    <div>
      <div class="container">
        <p>Contact</p>
        <div class="row">
          <div class="col-9">
            <p>Let's Chat</p>
            <p>Fill in your info in the form below and I look forward to hearing from you!</p>
          </div>
          <form style={{background:'pink'}}class="col-9">
            <div class="mb-3">
              <label for="exampleFormControlInput1" class="form-label">Name</label>
              <input value={fullName} onChange={handleInputChange}type="text" class="form-control" name="fullName" placeholder="Name"></input>
              <label for="exampleFormControlInput1" class="form-label">Email</label>
              <input value={email} onChange={handleInputChange} type="email" class="form-control" name="email" placeholder="email"></input>
            </div>
            <div class="mb-3">
              <label for="exampleFormControlTextarea1" class="form-label">Message</label>
              <textarea value={message}onChange={handleInputChange}class="form-control" name="message" rows="3"></textarea>
            </div>
            <div class="d-grid  col-4 mx-auto">
              <button onClick={handleFormSubmit} type="button" class="btn btn-outline-light">Light</button>
            </div>
          </form>
          <div style={{background:'orange'}}class="col-3 ">.col-6 Subsequent columns continue along the new line.</div>
        </div>
      </div>
    </div>
  );
}
