import React, { useState } from 'react';

export default function Contact() {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  const handleInputChange = (e) => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = e.target;

    // Ternary statement that will call either setFirstName or setLastName based on what field the user is typing in
    return name === 'fulltName' ? setFullName(value) : setEmail(value);
  };

  const handleFormSubmit = (e) => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    e.preventDefault();

    // Alert the user their first and last name, clear the inputs
    alert(`Hello ${fullName} ${email}`);
    setFullName('');
    setEmail('');
  };
  return (
    <div>
      <div class="container">
        <p>Contact</p>
        <div class="row">
          <div class="col-9">
            <p>Let's Chat</p>
            <p>Fill in your info in the form below and I look forward to hearing from you!</p>
          </div>
          <div style={{background:'pink'}}class="col-9">.col-4 Since 9 + 4 = 13 &gt; 12, this 4-column-wide div gets wrapped onto a new line as one contiguous unit.</div>
          <div style={{background:'orange'}}class="col-4">.col-6 Subsequent columns continue along the new line.</div>
        </div>
      </div>
    </div>
  );
}
